INSERT INTO `recipes` (`recipe_id`,`name`,`instructions`,`image_url`,`is_deleted`,`created_at`,`deleted_at`,`deleted_by`,`recovered_at`,`recovered_by`,`account_id`,`status`,`status_changed_at`,`status_changed_by`) VALUES (3,'Fruit salad','Ingredients\r\nSauce:\r\n\r\n⅔ cup fresh orange juice\r\n\r\n⅓ cup fresh lemon juice\r\n\r\n⅓ cup packed brown sugar\r\n\r\n½ teaspoon grated orange zest\r\n\r\n½ teaspoon grated lemon zest\r\n\r\n1 teaspoon vanilla extract\r\n\r\nSalad:\r\n\r\n2 cups cubed fresh pineapple\r\n\r\n2 cups strawberries, hulled and sliced\r\n\r\n3 kiwi fruit, peeled and sliced\r\n\r\n3 bananas, sliced\r\n\r\n2 oranges, peeled and sectioned\r\n\r\n1 cup seedless grapes\r\n\r\n2 cups blueberries\r\n\r\nDirections:\r\n\r\nFor the sauce: Bring orange juice, lemon juice, brown sugar, orange zest, and lemon zest to a boil in a saucepan over medium-high heat. Reduce heat to medium-low and simmer until slightly thickened, about 5 minutes. Remove from heat and stir in vanilla extract. Set aside to cool.\r\n\r\nFor the salad: Layer fruit in a large, clear glass bowl in this order: pineapple, strawberries, kiwi fruit, bananas, oranges, grapes, and blueberries. Pour cooled sauce over fruit; cover and refrigerate for 3 to 4 hours before serving.','static/recipes-img-table/fruit_salad.jpg',0,'2023-12-18 03:15:01','2023-12-18 03:19:58',1,'2023-12-18 03:20:06',1,2,'approved','2023-12-18 03:18:28',1);
INSERT INTO `recipes` (`recipe_id`,`name`,`instructions`,`image_url`,`is_deleted`,`created_at`,`deleted_at`,`deleted_by`,`recovered_at`,`recovered_by`,`account_id`,`status`,`status_changed_at`,`status_changed_by`) VALUES (4,'Biko','Ingredients\r\n\r\n    4 cups uncooked glutinous white rice\r\n\r\n    6 cups cold water\r\n\r\n    1 (14 ounce) can coconut milk, divided\r\n\r\n    1 ⅓ cups white sugar\r\n\r\n    1 ⅓ cups brown sugar\r\n\r\n    3 tablespoons coconut preserves (such as Phil Supreme®)\r\n\r\n\r\nstep 1:\r\nSoak rice overnight in the water.\r\n\r\nstep 2:\r\nPreheat oven to 350 degrees F (175 degrees C). Grease a 9x13 inch baking pan.\r\n\r\nstep 3:\r\nCook the soaked rice over medium heat in a saucepan with the soaking water, 1/2 can of coconut milk, and white sugar, stirring constantly, until tender and thickened, about 20 minutes. Pour into the prepared baking pan.\r\n\r\nstep 4:\r\nStir together the brown sugar, remaining 1/2 can of coconut milk, and coconut preserves in a small saucepan, and bring to a boil. Pour the mixture carefully over the rice.\r\n\r\nstep 5:\r\nBake in the preheated oven until the topping thickens, about 25 minutes. Cool completely; cut into squares to serve. ','static/recipes-img-table/biko.jpg',0,'2023-12-18 03:15:31','2023-12-18 03:20:37',3,'2023-12-18 03:20:46',3,2,'approved','2023-12-18 03:18:25',1);
INSERT INTO `recipes` (`recipe_id`,`name`,`instructions`,`image_url`,`is_deleted`,`created_at`,`deleted_at`,`deleted_by`,`recovered_at`,`recovered_by`,`account_id`,`status`,`status_changed_at`,`status_changed_by`) VALUES (5,'Leche Flan','Ingredients\r\n\r\nCaramel:\r\n\r\n    1 cup sugar\r\n\r\n    ¾ cup water\r\n\r\nCustard:\r\n\r\n    1 (13 ounce) can evaporated milk\r\n\r\n    1 (13 ounce) can sweetened condensed milk\r\n\r\n    12 egg yolks\r\n\r\n    1 tablespoon vanilla extract\r\n\r\nStep 1:\r\n    Preheat the oven to 325 degrees F (165 degrees C).\r\n\r\nStep 2:\r\n    Combine sugar and water in a saucepan over medium-high heat. Bring to a boil without stirring to avoid the mixture crystallizing. Continue to boil, swirling the pan occasionally, until sugar syrup begins to brown, 7 to 10 minutes. Reduce the heat to medium low and watch the color. Once the caramel is a deep brown, immediately pour it into a flan mold. Carefully tilt the mold to make sure the whole bottom surface is covered.\r\n\r\nStep 3:\r\n    Combine evaporated milk, condensed milk, egg yolks, and vanilla extract in a large bowl. Stir lightly to prevent bubbles or foam from forming. Strain batter slowly into the caramel-lined flan mold. Cover with aluminum foil.\r\n\r\nStep 4:\r\n    Place a baking dish large enough to hold the flan mold on the oven rack. Set the covered flan in the baking dish and add enough hot water to come halfway up the side of the mold.\r\n\r\nStep 5:\r\n    Bake in the preheated oven until firm, about 1 hour. Carefully remove the flan mold from the oven. Let cool, about 30 minutes. Run a paring knife around the edges of the flan. Cover the mold with a rimmed platter and carefully flip it over to release the flan from the mold for serving. ','static/recipes-img-table/leche-flan.jpg',0,'2023-12-18 03:15:58',NULL,NULL,NULL,NULL,2,'approved','2023-12-18 03:18:07',3);
INSERT INTO `recipes` (`recipe_id`,`name`,`instructions`,`image_url`,`is_deleted`,`created_at`,`deleted_at`,`deleted_by`,`recovered_at`,`recovered_by`,`account_id`,`status`,`status_changed_at`,`status_changed_by`) VALUES (6,'Kutsinta','Instructions\r\n\r\nInstructions\r\n\r\nIn a bowl, combine flour, tapioca flour, sugar, and water. Stir well until dissolved and mixture is smooth.\r\nAdd atsuete and stir until well dispersed and the desired color is achieved. \r\nAdd lye water and stir. \r\nLightly grease inside of molds with melted butter (If using silicone molds, skip this step). Fill molds to about ¾ full.\r\nAdd water to the steamer and bring to simmer over medium heat.\r\nSteam kutsinta for about 40 to 45 minutes or until mixture is set. Remove from heat and allow to cool. \r\nGently remove from molds and serve with grated coconut.','static/recipes-img-table/Kutsinta.jpg',0,'2023-12-18 03:16:57',NULL,NULL,NULL,NULL,1,'approved','2023-12-18 03:18:03',3);
INSERT INTO `recipes` (`recipe_id`,`name`,`instructions`,`image_url`,`is_deleted`,`created_at`,`deleted_at`,`deleted_by`,`recovered_at`,`recovered_by`,`account_id`,`status`,`status_changed_at`,`status_changed_by`) VALUES (7,'French apple cake','Ingredients\r\n\r\n    3 large baking apples (such as Honeycrisp)\r\n\r\n    2 teaspoons apple cider vinegar\r\n\r\n    1 cup all purpose flour\r\n\r\n    1 teaspoon baking powder\r\n\r\n    1 teaspoon kosher salt\r\n\r\n    1 stick unsalted butter, at room temperature\r\n\r\n    1/2 cup white sugar\r\n\r\n    2 tablespoons light brown sugar\r\n\r\n    2 large eggs\r\n\r\n    3 tablespoons dark rum\r\n\r\n    1/2 teaspoon vanilla extract\r\n\r\n    3 tablespoons white sugar for the top\r\n\r\n    1 tablespoon powdered sugar for dusting (optional)\r\n\r\nLocal Offers\r\n\r\n00000\r\nOops! We cannot find any ingredients on sale near you. Do we have the correct zip code?\r\nDirections\r\n\r\n    Preheat the oven to 350 degrees F (175 degrees C). Grease a 9-inch round cake pan with butter and line the bottom with parchment paper.\r\n\r\n    Peel (or leave skin on) and core apples; cut into cubes between 1/4 and 3/8-inch in size. A little smaller is okay, but if your cubes are any larger, they won’t get tender enough when baked.  Add to a large bowl and toss with apple cider vinegar.\r\n\r\n    Mix together flour, baking powder, and salt in a small bowl.\r\n\r\n    In another bowl, beat butter, 1/2 cup white sugar, and brown sugar with an electric mixer until light and fluffy. Beat in 1 egg on high speed until it is fully incorporated and the mixture is smooth. Add the second egg and repeat. Mix in rum, vanilla extract, and 1/2 of the flour mixture.  When the flour disappears add the rest, and fold in with a spatula.\r\n\r\n    Fold in apples until evenly mixed into the batter. Transfer batter to the prepared pan, making sure all the batter is pressed in firmly. Smooth top and dust with remaining 3 tablespoons sugar.\r\n\r\n    Bake in the preheated oven until a skewer inserted into the center comes out clean, about 45 minutes.\r\n\r\n    Remove cake from oven and let cool in the pan for about 30 minutes. Invert cooled cake onto a wire rack, place a serving plate on top, and flip back over. Dust top with powdered sugar and serve.','static/recipes-img-table/french-apple-cake-recipe-171299-2.jpg',0,'2023-12-18 03:17:45',NULL,NULL,NULL,NULL,3,'approved','2023-12-18 03:17:59',3);
